import React, { useState } from "react";
import "./App.css";

function App() {
  const [altitude, setAltitude] = useState("");
  const [his, setHis] = useState("");
  const [adi, setAdi] = useState("");

  const handleSubmit = async () => {
    const response = await fetch("api/flight-data", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        altitude: Number(altitude),
        his: Number(his),
        adi: Number(adi),
      }),
    });
    const data = await response.json();
    console.log(data);
  };

  return (
    <div className="container">
      <div className="input-box">
        <label>Altitude</label>
        <input
          type="number"
          value={altitude}
          onChange={(e) => setAltitude(e.target.value)}
        />
        <label>HIS</label>
        <input
          type="number"
          value={his}
          onChange={(e) => setHis(e.target.value)}
        />
        <label>ADI</label>
        <input
          type="number"
          value={adi}
          onChange={(e) => setAdi(e.target.value)}
        />
        <button onClick={handleSubmit}>SEND</button>
      </div>
      <div className="visual-box">
        <div className="altitude-scale">
          <div className="scale">3000</div>
          <div className="scale">2000</div>
          <div className="scale">1000</div>
          <div className="scale">0</div>
        </div>
        <div className="his-display">
          <div className="circle">
            <div className="arrow">â†‘</div>
          </div>
        </div>
        <div className="adi-display">
          <div className="circle"></div>
        </div>
      </div>
    </div>
  );
}

export default App;
