import React from "react";
import "./App.css";

const FlightDisplay = ({ altitude, his, adi, mode }) => {
  // חישוב מיקום גובה (0-3000 => אחוז למעלה)
  const topPosition = `${100 - (altitude / 3000) * 100}%`;

  // קביעת צבע לפי ADI
  const adiClass =
    adi === 100 ? "adi-circle blue" : adi === 0 ? "adi-circle green" : "";

  return (
    <div className="flight-container">
      

      {mode !== "text" && (
        <div className="visual-section">
          {/* Altitude Scale */}
          <div className="altitude-scale">
            <div className="altitude-indicator" style={{ top: topPosition }}></div>
            <div className="scale">3000</div>
            <div className="scale">2000</div>
            <div className="scale">1000</div>
            <div className="scale">0</div>
          </div>

          {/* HIS */}
          <div className="his-display">
            <div className="circle his-circle" style={{ transform: `rotate(${his}deg)` }}>
              <div className="arrow" style={{ transform: `rotate(${-his}deg)` }}>▲</div>
              <div className="degree-label top" style={{ transform: `rotate(${-his}deg)` }}>0</div>
              <div className="degree-label right" style={{ transform: `rotate(${-his}deg)` }}>180</div>
              <div className="degree-label bottom" style={{ transform: `rotate(${-his}deg)` }}>90</div>
              <div className="degree-label left" style={{ transform: `rotate(${-his}deg)` }}>270</div>
            </div>
          </div>

          {/* ADI */}
          <div className="adi-display">
            <div className={`circle ${adiClass}`}></div>
          </div>
        </div>
      )}

      {/* TEXT mode only */}
      {mode === "text" && (
        <div className="data-section">
          <div className="data-box">Altitude <br /> {altitude}</div>
          <div className="data-box">HIS <br /> {his}</div>
          <div className="data-box">ADI <br /> {adi}</div>
        </div>
      )}
    </div>
  );
};

export default FlightDisplay;
