import React, { useState } from "react";
import "./App.css";

function App() {
  const [altitude, setAltitude] = useState("");
  const [his, setHis] = useState("");
  const [adi, setAdi] = useState("");

  const handleSubmit = async () => {
    const response = await fetch("api/flight-data", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        altitude: Number(altitude),
        his: Number(his),
        adi: Number(adi),
      }),
    });
    const data = await response.json();
    console.log(data);
  };

  return (
    <div className="flight-container">
      <div className="header">
        <button className="btn">TEXT</button>
        <button className="btn">VISUAL</button>
        <button className="btn">+</button>
      </div>
      <div className="visual-section">
        <div className="altitude-scale">
          <div className="scale">3000</div>
          <div className="scale">2000</div>
          <div className="scale">1000</div>
          <div className="scale">0</div>
        </div>
        <div className="his-display">
          <div className="circle">
            <div className="arrow" style={{ transform: `rotate(${his}deg)` }}>â–²</div>
            <div className="degree-label top">0</div>
            <div className="degree-label right">180</div>
            <div className="degree-label bottom">90</div>
            <div className="degree-label left">270</div>
          </div>
        </div>
        <div className="adi-display">
          <div className="circle"></div>
        </div>
      </div>
      <div className="data-section">
        <div className="data-box">Altitude <br /> {altitude}</div>
        <div className="data-box">HIS <br /> {his}</div>
        <div className="data-box">ADI <br /> {adi}</div>
      </div>
    </div>
  );
}

export default App;
