import React, { useState, useEffect } from "react";
import FlightDisplay from "./flightDisplay";
import "./App.css";

function App() {
  const [altitude, setAltitude] = useState(0);
  const [his, setHis] = useState(0);
  const [adi, setAdi] = useState(0);
  const [mode, setMode] = useState("none"); // "text" | "visual" | "input"

  let shouldSendMessage = true;

  function isAltitudeValid(altitude){
    return !(((0>altitude || altitude>3000)) && alert("Altitude out of bounce")) && setAltitude(altitude);
  };

  function isHisValid(his){
    return !(((0>his || his>360)) && alert("HIS out of bounce")) && setHis(his);
  };

  function isAdiValid(adi){
    return !(((-100>adi || adi>100)) && alert("Adi out of bounce")) && setAdi(adi);
  };

  const fetchData = async () => {
    try {
      const res = await fetch("/api/status");
      const status = await res.json();
      console.log("Server status:", status);

      const resData = await fetch("/api/flight-data/latest");
      const data = await resData.json();


      setAltitude(data.altitude.value);
      setHis(data.his.value);
      setAdi(data.adi.value);
    } catch (err) {
      console.error("Error fetching data", err);
    }
  };


  const handleSubmit = async () => {
    try {
      if(shouldSendMessage){
         await fetch("/api/flight-data", {
           method: "POST",
           headers: { "Content-Type": "application/json" },
           body: JSON.stringify({
             altitude,
             his,
             adi,
           }),
         });
         alert("Data submitted");
         setMode("visual");
        }
      else{
         alert("Data not submitted");   
         shouldSendMessage = true; 
      }
    } catch (err) {
      console.error("Submit failed", err);
    }
  };

  useEffect(() => {
    if (mode === "text" || mode === "visual") {
      fetchData();
    }
  }, [mode]);

  return (
    <div className="container">
      <div className="header">
        <button className="btn" onClick={() => setMode("text")}>TEXT</button>
        <button className="btn" onClick={() => setMode("visual")}>VISUAL</button>
        <button className="btn" onClick={() => setMode("input")}>+</button>
      </div>

      {mode === "input" && (
        <div className="input-box">
          <label>Altitude</label>
          <input
            type="number"
            value={altitude}
            onChange={(e) => shouldSendMessage = shouldSendMessage &&isAltitudeValid(Number(e.target.value))}
          />
          <label>HIS</label>
          <input
            type="number"
            value={his}
            onChange={(e) => shouldSendMessage = shouldSendMessage &&isHisValid(Number(e.target.value))}
          />
          <label>ADI</label>
          <input
            type="number"
            value={adi}
            onChange={(e) => shouldSendMessage = shouldSendMessage && isAdiValid(Number(e.target.value))}
          />
          <button onClick={handleSubmit}>SEND</button>
        </div>
      )}

      {(mode === "visual" || mode === "text") && (
        <FlightDisplay
          altitude={altitude}
          his={his}
          adi={adi}
          mode={mode}
        />
      )}
    </div>
  );
}

export default App;
